import React from "react";
import type { BoardData } from "../types/types";

interface Props {
  board: BoardData;
}

const Footer: React.FC<Props> = ({ board }) => {
  const activeCount = board.find((c) => c.id === "backlog")?.issues.length || 0;
  const finishedCount = board.find((c) => c.id === "finished")?.issues.length || 0;

  return (
    <footer
  style={{
    position: "fixed",
    bottom: 0,
    left: 0,
    width: "100%",
    background: "#0067A3",
    borderTop: "1px solid #ddd",
    display: "flex",
    justifyContent: "flex-start",
    alignItems: "center",
    padding: "10px 40px",
    color: "white",
  }}
>
  <div style={{ display: "flex", alignItems: "center" }}>
    <span style={{ marginRight: "36px" }}>
      Active tasks: <b>{activeCount}</b>
    </span>
    <span>
      Finished tasks: <b>{finishedCount}</b>
    </span>
  </div>
  <div style={{ marginLeft: "auto" }}>
    Created by: Your Name
  </div>
</footer>

  );
};

export default Footer;
