import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";

interface ColumnProps {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<ColumnProps> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");
  const [adding, setAdding] = useState(false);
  const [selectedTaskId, setSelectedTaskId] = useState("");

  // Получаем индекс текущей колонки
  const columnIndex = board.findIndex((col) => col.id === column.id);
  const prevColumn = board[columnIndex - 1]; // предыдущая колонка, если есть

  // Функция добавления новой задачи в Backlog
  const addTask = () => {
    if (!text.trim()) return;
    const newTask: Issue = { id: Date.now().toString(), name: text, description: "" };
    const newBoard = board.map((col) =>
      col.id === column.id ? { ...col, issues: [...col.issues, newTask] } : col
    );
    setBoard(newBoard);
    setText("");
    setAdding(false);
  };

  // Функция перемещения задачи из предыдущей колонки
  const moveTask = () => {
    if (!selectedTaskId) return;
    const taskToMove = prevColumn!.issues.find((t) => t.id === selectedTaskId);
    if (!taskToMove) return;

    const newBoard = board.map((col, i) => {
      if (i === columnIndex) {
        return { ...col, issues: [...col.issues, taskToMove] };
      }
      if (i === columnIndex - 1) {
        return { ...col, issues: col.issues.filter((t) => t.id !== selectedTaskId) };
      }
      return col;
    });

    setBoard(newBoard);
    setSelectedTaskId("");
    setAdding(false);
  };

  return (
    <div style={{ background: "#EBECF0", padding: "12px", width: "282px", borderRadius: "10px" }}>
      <h2>{column.title}</h2>

      {column.issues.map((task) => (
        <Link
          key={task.id}
          to={`/tasks/${task.id}`}
          style={{
            display: "block",
            background: "#fff",
            padding: "7px 8px",
            marginBottom: "15px",
            borderRadius: "5px",
            textDecoration: "none",
            color: "black",
            cursor: "pointer",
          }}
        >
          {task.name}
        </Link>
      ))}

      {column.id === "backlog" && (
        <div style={{ marginTop: "10px" }}>
          {adding && (
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Task name"
              style={{ width: "100%", marginBottom: "5px" }}
            />
          )}
          <button onClick={adding ? addTask : () => setAdding(true)}>
            {adding ? "Submit" : "Add card"}
          </button>
        </div>
      )}

      {column.id !== "backlog" && (
        <div style={{ marginTop: "10px" }}>
          <button
            onClick={() => setAdding(true)}
            disabled={!prevColumn || prevColumn.issues.length === 0}
            style={{
              marginBottom: "5px",
              background: !prevColumn || prevColumn.issues.length === 0 ? "#ccc" : "#5E6C84",
              color: "white",
              border: "none",
              cursor: !prevColumn || prevColumn.issues.length === 0 ? "not-allowed" : "pointer",
            }}
          >
            Add card
          </button>

          {adding && prevColumn && prevColumn.issues.length > 0 && (
            <div style={{ marginTop: "5px" }}>
              <select
                value={selectedTaskId}
                onChange={(e) => setSelectedTaskId(e.target.value)}
                style={{ width: "100%", marginBottom: "5px" }}
              >
                <option value="">Select task</option>
                {prevColumn.issues.map((t) => (
                  <option key={t.id} value={t.id}>
                    {t.name}
                  </option>
                ))}
              </select>
              <button onClick={moveTask}>Move</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Column;
