import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { BoardData } from "./types/types";
import { loadBoard, saveBoard } from "./utils/localStorage";
import KanbanBoard from "./components/KanbanBoard";
import TaskDetailPage from "./components/TaskDetailPage";
import Footer from "./components/Footer";
import UserMenu from "./components/UserMenu";
import "./App.css";

const initialData: BoardData = [
  { id: "backlog", title: "Backlog", issues: [{ id: "12345", name: "Sprint bugfix", description: "Fix all the bugs" }] },
  { id: "ready", title: "Ready", issues: [] },
  { id: "in-progress", title: "In Progress", issues: [] },
  { id: "finished", title: "Finished", issues: [] },
];

const App: React.FC = () => {
  const [board, setBoard] = useState<BoardData>(() => loadBoard() || initialData);

  useEffect(() => {
    saveBoard(board);
  }, [board]);

  return (
    <BrowserRouter>
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "7px 16px 8px 14px", background: "#0067A3", height: "55px"}}>
        <span style={{padding: "6px 0 13px 6px"}}>
          Awesome Kanban Board</span>
        <UserMenu />
      </header>

        <Routes>
          <Route
            path="/"
            element={<KanbanBoard board={board} setBoard={setBoard} />}
          />
          <Route
            path="/tasks/:id"
            element={<TaskDetailPage board={board} setBoard={setBoard} />}
          />
        </Routes>

      <Footer board={board} />
    </BrowserRouter>
  );
};

export default App;
