import React from "react";
import type { BoardData } from "../types/types";

interface Props {
  board: BoardData;
}

const Footer: React.FC<Props> = ({ board }) => {
  const activeCount = board.find((c) => c.id === "backlog")?.issues.length || 0;
  const finishedCount = board.find((c) => c.id === "finished")?.issues.length || 0;

  return (
    <footer style={{ position: "fixed", bottom: 0, left: 0, width: "100%", background: "#fff", borderTop: "1px solid #ddd", display: "flex", justifyContent: "space-between", padding: "10px 40px" }}>
      <div>Active tasks: <b>{activeCount}</b></div>
      <div>Finished tasks: <b>{finishedCount}</b></div>
    </footer>
  );
};

export default Footer;
