import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";
import styles from "./Column.module.css";

interface Props {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<Props> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");
  const [selectedTask, setSelectedTask] = useState<Issue | null>(null);
  const [adding, setAdding] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const order = ["backlog", "ready", "in-progress", "finished"];

  const prevColumn = board.find(
    (col) => order.indexOf(col.id) === order.indexOf(column.id) - 1
  );

  const addToBacklog = () => {
    if (!text.trim()) return;

    const newTask: Issue = {
      id: Date.now().toString(),
      name: text,
      description: "",
    };

    setBoard(
      board.map((col) =>
        col.id === "backlog"
          ? { ...col, issues: [...col.issues, newTask] }
          : col
      )
    );

    setText("");
    setAdding(false);
  };

  const moveTask = () => {
    if (!selectedTask || !prevColumn) return;

    setBoard(
      board.map((col) => {
        if (col.id === prevColumn.id) {
          return {
            ...col,
            issues: col.issues.filter((t) => t.id !== selectedTask.id),
          };
        }

        if (col.id === column.id) {
          return {
            ...col,
            issues: [...col.issues, selectedTask],
          };
        }

        return col;
      })
    );

    setSelectedTask(null);
    setDropdownOpen(false);
    setAdding(false);
  };

  const isDisabled =
    column.id !== "backlog" &&
    (!prevColumn || prevColumn.issues.length === 0);

  return (
    <div className={styles.column}>
      <h2 className={styles.title}>{column.title}</h2>

      {column.issues.map((task) => (
        <Link key={task.id} to={`/tasks/${task.id}`} className={styles.card}>
          {task.name}
        </Link>
      ))}

      {/* BACKLOG INPUT */}
      {adding && column.id === "backlog" && (
        <input
          className={styles.input}
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Task name"
        />
      )}

      {/* CUSTOM DROPDOWN */}
      {adding && column.id !== "backlog" && prevColumn && (
        <div className={styles.dropdown}>
          <div
            className={styles.dropdownHeader}
            onClick={() => setDropdownOpen(!dropdownOpen)}
          >
            {selectedTask ? selectedTask.name : "Select task"}
            <span
              className={`${styles.arrow} ${
                dropdownOpen ? styles.arrowUp : ""
              }`}
            />
          </div>

          {dropdownOpen && (
            <div className={styles.dropdownList}>
              {prevColumn.issues.map((task) => (
                <div
                  key={task.id}
                  className={styles.dropdownItem}
                  onClick={() => {
                    setSelectedTask(task);
                    setDropdownOpen(false);
                  }}
                >
                  {task.name}
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      <button
        disabled={isDisabled}
        onClick={
          adding
            ? column.id === "backlog"
              ? addToBacklog
              : moveTask
            : () => setAdding(true)
        }
        className={`
          ${styles.button}
          ${adding ? styles.buttonSubmit : ""}
          ${isDisabled ? styles.buttonDisabled : ""}
        `}
      >
        {adding ? "Submit" : "Add card"}
      </button>
    </div>
  );
};

export default Column;
