import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";

interface ColumnProps {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<ColumnProps> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");

  const addTask = () => {
    if (!text.trim()) return;

    const newTask: Issue = {
      id: Date.now().toString(),
      name: text,
      description: "",
    };

    const newBoard = board.map((col) =>
      col.id === column.id
        ? { ...col, issues: [...col.issues, newTask] }
        : col
    );

    setBoard(newBoard);
    setText("");
  };

  return (
    <div style={{ background: "#eee", padding: "10px", width: "250px", borderRadius: "8px" }}>
      <h2>{column.title}</h2>

      {column.issues.map((task: Issue) => (
        <div key={task.id} style={{ marginBottom: "6px" }}>
          <Link to={`/tasks/${task.id}`} style={{ textDecoration: "none", color: "black" }}>
            {task.name}
          </Link>
        </div>
      ))}

      {column.id === "backlog" && (
        <div style={{ marginTop: "10px" }}>
          <input
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="New task"
            style={{ width: "100%", marginBottom: "5px", padding: "4px" }}
          />
          <button onClick={addTask} style={{ width: "100%" }}>Add</button>
        </div>
      )}
    </div>
  );
};

export default Column;
