import React from "react";
import type { BoardData } from "../types/types";

interface FooterProps {
  board: BoardData;
}

const Footer: React.FC<FooterProps> = ({ board }) => {
  const backlogColumn = board.find(col => col.id === "backlog");
  const finishedColumn = board.find(col => col.id === "finished");

  const activeCount = backlogColumn ? backlogColumn.issues.length : 0;
  const finishedCount = finishedColumn ? finishedColumn.issues.length : 0;

  return (
    <footer
      style={{
        position: "fixed",
        bottom: 0,
        left: 0,
        width: "100%",
        background: "#fff",
        borderTop: "1px solid #ddd",
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "10px 40px",
      }}
    >
      <div>Active tasks: <b>{activeCount}</b></div>
      <div>Finished tasks: <b>{finishedCount}</b></div>
    </footer>
  );
};

export default Footer;
