import React, { useState } from "react";
import type { ColumnType, BoardData } from "../types/types";

interface ColumnProps {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<ColumnProps> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");

  const addTask = () => {
    if (!text.trim()) return;

    const newTask = { id: Date.now().toString(), name: text, description: "" };
    const newBoard = board.map((col) =>
      col.id === column.id ? { ...col, issues: [...col.issues, newTask] } : col
    );

    setBoard(newBoard);
    setText("");
  };

  return (
    <div style={{ background: "#eee", padding: "10px", width: "250px" }}>
      <h2>{column.title}</h2>
      {column.issues.map((task) => (
        <div key={task.id}>{task.name}</div>
      ))}
      {column.id === "backlog" && (
        <>
          <input
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="New task"
          />
          <button onClick={addTask}>Add</button>
        </>
      )}
    </div>
  );
};

export default Column;
