import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import type { BoardData } from "../types/types";

interface Props {
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const TaskDetailPage: React.FC<Props> = ({ board, setBoard }) => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const task = board
    .flatMap((col) => col.issues)
    .find((issue) => issue.id === id);

  const [desc, setDesc] = useState(task?.description || "");

  if (!task) return <div>Task not found</div>;

  const saveDescription = () => {
    setBoard(
      board.map((col) => ({
        ...col,
        issues: col.issues.map((issue) =>
          issue.id === id ? { ...issue, description: desc } : issue
        ),
      }))
    );
  };

  return (
    <div style={{ padding: "20px" }}>
      {/* Карточка */}
      <div
        style={{
          background: "#ffffff",
          borderRadius: "8px",
          padding: "20px",
          maxWidth: "600px",
        }}
      >
        {/* Верхняя строка: название + крестик */}
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-start",
            marginBottom: "16px",
          }}
        >
          <h1 style={{ margin: 0, fontSize: "24px" }}>{task.name}</h1>

          <button
            onClick={() => navigate("/")}
            style={{
              background: "transparent",
              border: "none",
              fontSize: "20px",
              cursor: "pointer",
              lineHeight: 1,
            }}
          >
            ✕
          </button>
        </div>

        {/* Описание */}
        <textarea
          value={desc}
          onChange={(e) => setDesc(e.target.value)}
          onBlur={saveDescription}
          placeholder="This task has no description"
          style={{
            width: "100%",
            minHeight: "120px",
            resize: "vertical",
            borderRadius: "6px",
            padding: "10px",
          }}
        />
      </div>
    </div>
  );
};

export default TaskDetailPage;
