import { BoardData } from './types/types';
import './App.css';
import { useEffect, useState } from 'react';
import { loadBoard, saveBoard} from './utils/localStorage';
import { BrowserRouter, Routes } from 'react-router-dom';
import KanbanBoard from './components/KanbanBoard';


const initialData: BoardData = [
  {
    id: "backlog",
    title: "Backlog",
    issues: [{ id: "12345", name: "Sprint bugfix", description: "Fix all the bugs" }],
  },
  { id: "ready", title: "Ready", issues: [] },
  { id: "in-progress", title: "In Progress", issues: [] },
  { id: "finished", title: "Finished", issues: [] },
];

const App: React.FC = () =>{
  const [board, setBoard] = useState<BoardData>(() =>  loadBoard() || initialData);

  useEffect(() =>{
    saveBoard(board);
   },[board]);



   return(
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<KanbanBoard board={board} setBoard={setBoard} />} />
        <Route path="/tasks/:id" element={<TaskDetailPage board={board} setBoard={setBoard} />} />
      </Routes>
    </BrowserRouter>

   );

};







export default App;
