import React from "react";
import type { BoardData } from "../types/types";

interface Props {
  board: BoardData;
}

const Footer: React.FC<Props> = ({ board }) => {
  const activeCount = board.find((c) => c.id === "backlog")?.issues.length || 0;
  const finishedCount = board.find((c) => c.id === "finished")?.issues.length || 0;

  return (
    <footer
      style={{
        position: "fixed",
        bottom: 0,
        left: 0,
        width: "100%",
        background: "#0067A3",
        borderTop: "1px solid #ddd",
        display: "flex",
        justifyContent: "space-between",
        padding: "10px 40px",
        color: "white",
      }}
    >
      <div>
        Active tasks: <b>{activeCount}</b> | Finished tasks: <b>{finishedCount}</b>
      </div>
      <div>
        Created by: Your Name
      </div>
    </footer>
  );
};

export default Footer;
