import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";

interface ColumnProps {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<ColumnProps> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");
  const [adding, setAdding] = useState(false); // состояние, показывать ли поле ввода

  const addTask = () => {
    if (!text.trim()) return; // не добавляем пустую задачу

    const newTask: Issue = {
      id: Date.now().toString(),
      name: text,
      description: "",
    };

    const newBoard = board.map((col) =>
      col.id === column.id ? { ...col, issues: [...col.issues, newTask] } : col
    );

    setBoard(newBoard);
    setText(""); // очищаем поле
    setAdding(false); // скрываем поле после добавления
  };

  return (
    <div style={{ 
      background: "#EBECF0",
      padding: "12px",
      width: "282px",
      borderRadius: "10px",
       }}>
      <h2>{column.title}</h2>
      {column.issues.map((task) => (
  <Link
    key={task.id}
    to={`/tasks/${task.id}`}
    style={{
      width: "258px", 
      display: "block",
      background: "#fff",
      padding: "7px 8px",
      marginBottom: "15px",
      borderRadius: "5px",
      textDecoration: "none",
      color: "black",
      cursor: "pointer",
    }}
  >
    {task.name}
  </Link>
))}



      {column.id === "backlog" && (
        <div style={{ marginTop: "10px" }}>
          {adding && (
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Task name"
              style={{ width: "100%", marginBottom: "5px" }}
            />
          )}
          <button onClick={adding ? addTask : () => setAdding(true)}
          style={{
            fontSize: "18px",
            gap: "3px",
            border: "none",
            color: "#5E6C84",
            marginBottom: "8px",
           

          }}
            >
            {!adding && <span style={{ fontSize: "16px" }}><svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M13 6H8V1C8 0.448 7.552 0 7 0C6.448 0 6 0.448 6 1V6H1C0.448 6 0 6.448 0 7C0 7.552 0.448 8 1 8H6V13C6 13.552 6.448 14 7 14C7.552 14 8 13.552 8 13V8H13C13.552 8 14 7.552 14 7C14 6.448 13.552 6 13 6Z" fill="#5E6C84"/>
</svg>
</span>}
            {adding ? "Submit" : " Add card"}
          </button>
        </div>
      )}
    </div>
  );
};

export default Column;
