import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import type { BoardData, ColumnType } from "../types/types";

interface Props {
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const TaskDetailPage: React.FC<Props> = ({ board, setBoard }) => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const task = board.flatMap((col) => col.issues).find((issue) => issue.id === id);

  if (!task) return <div>Task not found</div>;

  const [desc, setDesc] = useState(task.description || "");

  const saveDescription = () => {
    setBoard(
      board.map((col) => ({
        ...col,
        issues: col.issues.map((issue) =>
          issue.id === id ? { ...issue, description: desc } : issue
        ),
      }))
    );
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={() => navigate("/")}>âœ• Back</button>

      <h1>{task.name}</h1>

      <textarea
        value={desc}
        onChange={(e) => setDesc(e.target.value)}
        onBlur={saveDescription}
        placeholder="This task has no description"
        style={{ width: "100%", minHeight: "100px", marginTop: "10px" }}
      />
    </div>
  );
};

export default TaskDetailPage;
