import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";

interface ColumnProps {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<ColumnProps> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");
  const [adding, setAdding] = useState(false);
  const [selectedTaskId, setSelectedTaskId] = useState("");

  const columnIndex = board.findIndex((col) => col.id === column.id);
  const prevColumn = board[columnIndex - 1];

  const addTask = () => {
    if (!text.trim()) return;
    const newTask: Issue = { id: Date.now().toString(), name: text, description: "" };
    setBoard(
      board.map((col) =>
        col.id === column.id ? { ...col, issues: [...col.issues, newTask] } : col
      )
    );
    setText("");
    setAdding(false);
  };

  const moveTask = () => {
    if (!selectedTaskId || !prevColumn) return;
    const taskToMove = prevColumn.issues.find((t) => t.id === selectedTaskId);
    if (!taskToMove) return;

    const newBoard = board.map((col, i) => {
      if (i === columnIndex) return { ...col, issues: [...col.issues, taskToMove] };
      if (i === columnIndex - 1) return { ...col, issues: col.issues.filter((t) => t.id !== selectedTaskId) };
      return col;
    });

    setBoard(newBoard);
    setSelectedTaskId("");
    setAdding(false);
  };

  return (
    <div style={{ background: "#EBECF0", padding: "12px", width: "282px", borderRadius: "10px" }}>
      <h2>{column.title}</h2>

      {column.issues.map((task) => (
        <Link
          key={task.id}
          to={`/tasks/${task.id}`}
          style={{
            display: "block",
            background: "#fff",
            padding: "7px 8px",
            marginBottom: "15px",
            borderRadius: "5px",
            textDecoration: "none",
            color: "black",
            cursor: "pointer",
          }}
        >
          {task.name}
        </Link>
      ))}

      {/* Backlog */}
      {column.id === "backlog" && (
        <div style={{ marginTop: "10px" }}>
          {adding && (
            <input
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Task name"
              style={{ width: "100%", marginBottom: "5px" }}
            />
          )}
          <button
            onClick={adding ? addTask : () => setAdding(true)}
            style={{
              fontSize: "18px",
              gap: "3px",
              border: "none",
              color: "#5E6C84",
              display: "flex",
              alignItems: "center",
              cursor: "pointer",
            }}
          >
            {!adding && (
              <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path
                  d="M13 6H8V1C8 0.448 7.552 0 7 0C6.448 0 6 0.448 6 1V6H1C0.448 6 0 6.448 0 7C0 7.552 0.448 8 1 8H6V13C6 13.552 6.448 14 7 14C7.552 14 8 13.552 8 13V8H13C13.552 8 14 7.552 14 7C14 6.448 13.552 6 13 6Z"
                  fill="#5E6C84"
                />
              </svg>
            )}
            {adding ? "Submit" : "Add card"}
          </button>
        </div>
      )}

      {/* Остальные колонки */}
      {column.id !== "backlog" && (
        <div style={{ marginTop: "10px" }}>
          <button
            onClick={() => setAdding(true)}
            disabled={!prevColumn || prevColumn.issues.length === 0}
            style={{
              display: "flex",
              alignItems: "center",
              fontSize: "16px",
              gap: "4px",
              border: "none",
              color: "white",
              background: !prevColumn || prevColumn.issues.length === 0 ? "#ccc" : "#5E6C84",
              cursor: !prevColumn || prevColumn.issues.length === 0 ? "not-allowed" : "pointer",
              padding: "6px 10px",
              borderRadius: "4px",
            }}
          >
            {!adding && (
              <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path
                  d="M13 6H8V1C8 0.448 7.552 0 7 0C6.448 0 6 0.448 6 1V6H1C0.448 6 0 6.448 0 7C0 7.552 0.448 8 1 8H6V13C6 13.552 6.448 14 7 14C7.552 14 8 13.552 8 13V8H13C13.552 8 14 7.552 14 7C14 6.448 13.552 6 13 6Z"
                  fill="white"
                />
              </svg>
            )}
            Add card
          </button>

          {adding && prevColumn && prevColumn.issues.length > 0 && (
            <div style={{ marginTop: "5px" }}>
              <select
                value={selectedTaskId}
                onChange={(e) => setSelectedTaskId(e.target.value)}
                style={{ width: "100%", marginBottom: "5px", padding: "4px" }}
              >
                <option value="">Select task</option>
                {prevColumn.issues.map((t) => (
                  <option key={t.id} value={t.id}>
                    {t.name}
                  </option>
                ))}
              </select>
              <button
                onClick={moveTask}
                style={{
                  width: "100%",
                  background: "#5E6C84",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  padding: "6px",
                  cursor: "pointer",
                }}
              >
                Move
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Column;
