import React, { useState } from "react";
import type { ColumnType, BoardData, Issue } from "../types/types";
import { Link } from "react-router-dom";

interface Props {
  column: ColumnType;
  board: BoardData;
  setBoard: (board: BoardData) => void;
}

const Column: React.FC<Props> = ({ column, board, setBoard }) => {
  const [text, setText] = useState("");
  const [selectedId, setSelectedId] = useState("");
  const [adding, setAdding] = useState(false);

  const order = ["backlog", "ready", "in-progress", "finished"];
  const prevColumn = board.find(
    (col) => order.indexOf(col.id) === order.indexOf(column.id) - 1
  );

  const addToBacklog = () => {
    if (!text.trim()) return;

    const newTask: Issue = {
      id: Date.now().toString(),
      name: text,
      description: "",
    };

    setBoard(
      board.map((col) =>
        col.id === "backlog"
          ? { ...col, issues: [...col.issues, newTask] }
          : col
      )
    );

    setText("");
    setAdding(false);
  };

  const moveTask = () => {
    if (!selectedId || !prevColumn) return;

    const task = prevColumn.issues.find((t) => t.id === selectedId);
    if (!task) return;

    setBoard(
      board.map((col) => {
        if (col.id === prevColumn.id)
          return { ...col, issues: col.issues.filter((t) => t.id !== task.id) };

        if (col.id === column.id)
          return { ...col, issues: [...col.issues, task] };

        return col;
      })
    );

    setSelectedId("");
    setAdding(false);
  };

  return (
    <div style={{ background: "#EBECF0", padding: "12px", width: "282px", borderRadius: "10px" }}>
      <h2>{column.title}</h2>

      {column.issues.map((task) => (
        <Link
          key={task.id}
          to={`/tasks/${task.id}`}
          style={{
            display: "block",
            background: "#fff",
            padding: "8px",
            marginBottom: "10px",
            borderRadius: "6px",
            textDecoration: "none",
            color: "black",
          }}
        >
          {task.name}
        </Link>
      ))}

      {adding && column.id === "backlog" && (
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Task name"
          style={{ width: "100%", marginBottom: "5px" }}
        />
      )}

      {adding && column.id !== "backlog" && prevColumn && (
        <select
          value={selectedId}
          onChange={(e) => setSelectedId(e.target.value)}
          style={{ width: "100%", marginBottom: "5px" }}
        >
          <option value="">Select task</option>
          {prevColumn.issues.map((task) => (
            <option key={task.id} value={task.id}>
              {task.name}
            </option>
          ))}
        </select>
      )}

<button
  disabled={
    column.id !== "backlog" &&
    (!prevColumn || prevColumn.issues.length === 0)
  }
  onClick={
    adding
      ? column.id === "backlog"
        ? addToBacklog
        : moveTask
      : () => setAdding(true)
  }
  style={{
    fontSize: "18px",
    gap: "6px",
    border: "none",
    color:
      column.id !== "backlog" &&
      (!prevColumn || prevColumn.issues.length === 0)
        ? "#A5ADBA"
        : "#5E6C84",
    background: "transparent",
    cursor:
      column.id !== "backlog" &&
      (!prevColumn || prevColumn.issues.length === 0)
        ? "not-allowed"
        : "pointer",
    display: "flex",
    alignItems: "center",
  }}
>
  {!adding && (
    <span style={{ display: "flex", alignItems: "center" }}>
      <svg
        width="14"
        height="14"
        viewBox="0 0 14 14"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M13 6H8V1C8 0.448 7.552 0 7 0C6.448 0 6 0.448 6 1V6H1C0.448 6 0 6.448 0 7C0 7.552 0.448 8 1 8H6V13C6 13.552 6.448 14 7 14C7.552 14 8 13.552 8 13V8H13C13.552 8 14 7.552 14 7C14 6.448 13.552 6 13 6Z"
          fill="currentColor"
        />
      </svg>
    </span>
  )}

  {adding ? "Submit" : "Add card"}
</button>

    </div>
  );
};

export default Column;
